int dis ( int a , int b ) { if ( a - b > 0 ) { return a - b ; } else { return b - a ; } } int main ( ) { int t , i , n , j , a , k , b , c ; char arr [ 50000 ] ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { a = 0 ; b = 0 ; c = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & arr [ j ] ) ; } for ( j = 0 ; j < n - 1 ; j ++ ) { for ( k = 0 ; k < n - 1 ; k ++ ) { if ( ( arr [ k ] != 'a' ) && ( arr [ k + 1 ] != 'a' ) ) { if ( a < dis ( arr [ k ] , arr [ k + 1 ] ) ) { a = dis ( arr [ k ] , arr [ k + 1 ] ) ; b = k ; } } } if ( arr [ b ] > arr [ b + 1 ] ) { arr [ b ] = 'a' ; c = c + arr [ b + 1 ] ; } else { arr [ b + 1 ] = 'a' ; c = c + arr [ b ] ; } } printf ( "%d\n" , c ) ; } }