int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; int a , b , r ; long long int l ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; r = gcd ( a , b ) ; l = ( a * b ) / r ; printf ( "%d %lld\n" , r , l ) ; } return 0 ; }