int main ( ) { unsigned int n , i , j , s1 , s2 , m ; int a [ 100 ] [ 100 ] ; scanf ( "%u" , & n ) ; while ( n -- ) { scanf ( "%u" , & m ) ; s1 = 0 ; s2 = 0 ; for ( i = 0 ; i < m ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; if ( a [ i ] [ j ] > 90 || a [ i ] [ j ] < 0 ) exit ( 1 ) ; } s1 += a [ i ] [ 0 ] ; s2 += a [ i ] [ i ] ; } if ( s1 > s2 ) printf ( "%d\n" , s1 ) ; else printf ( "%d\n" , s2 ) ; } return 0 ; }