int main ( ) { int A , B , gcd , lcm , numerator , denominator , remainder ; printf ( "enter two numbers:" ) ; scanf ( "%d%d" , & A , & B ) ; if ( A > B ) { denominator = A ; numerator = B ; } else { numerator = A ; denominator = B ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = A * B / gcd ; printf ( "gcd of %d and %d= %d" , gcd ) ; printf ( "lcm of %d and %d =%d" , lcm ) ; return 0 ; }