int main ( void ) { int n , * * a , nr , i , max = - 99999 , up , dia , x , j ; scanf ( "%d" , & n ) ; while ( n > 0 ) { scanf ( "%d" , & nr ) ; a = ( int * * ) malloc ( ( nr + 1 ) * sizeof ( int * ) ) ; for ( i = 0 ; i <= nr ; i ++ ) { a [ i ] = ( int * ) malloc ( ( nr + 1 ) * sizeof ( int ) ) ; } for ( i = 1 ; i <= nr ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & x ) ; up = ( x + a [ i - 1 ] [ j ] ) ; dia = ( x + a [ i - 1 ] [ j - 1 ] ) ; a [ i ] [ j ] = ( up > dia ? up : dia ) ; } } for ( i = 0 ; i < nr ; i ++ ) { if ( a [ nr ] [ i ] > max ) max = a [ nr ] [ i ] ; } n -- ; printf ( "%d" , max ) ; } return 0 ; }