int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , i , j , l = 0 , k , sum = 0 ; scanf ( "%d" , & a ) ; int b [ 100 ] [ 100 ] ; for ( i = 0 ; i < a ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & b [ i ] [ j ] ) ; } } for ( i = 0 ; i < a ; i ++ ) { if ( b [ i ] [ l ] >= b [ i ] [ l + 1 ] ) k = b [ i ] [ l ] ; else { l = l + 1 ; k = b [ i ] [ l ] ; } sum = sum + k ; } printf ( "%d\n" , sum ) ; } return 0 ; }