int min ( int a , int b ) { if ( a < b ) return a ; else return b ; } int max ( int a , int b ) { if ( a < b ) return b ; else return a ; } int main ( ) { int test , i = 1 , a , b , p , q ; scanf ( "%d" , & test ) ; while ( test -- ) { scanf ( "%d %d" , & a , & b ) ; q = min ( a , b ) ; p = max ( a , b ) ; while ( i != 0 ) { i = p % q ; p = q ; q = i ; } printf ( "%d " , p ) ; q = abs ( a * b ) / p ; printf ( "%d\n" , q ) ; i = 1 ; } return 0 ; }