int main ( void ) { int x , y , n1 , n2 , result , gcd , lcm , t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & n1 , & n2 ) ; x = n1 ; y = n2 ; while ( n1 != n2 ) { if ( n1 > n2 ) { n1 = n1 - n2 ; } else { n2 = n2 - n1 ; } } gcd = n1 ; lcm = ( x * y ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } }