int main ( ) { long long int n , t , i , k , j , b , b1 , b2 , gcd , lcm ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & n , & k ) ; b1 = n > k ? n : k ; b2 = n < k ? n : k ; b = ( b1 / 2 ) > b2 ? ( b1 / 2 ) : b2 ; for ( j = 1 ; j <= b ; j ++ ) { if ( n % j == 0 && k % j == 0 ) gcd = j ; } for ( j = 1 ; j <= b2 ; j ++ ) { b = ( b1 * j ) ; if ( b % b2 == 0 ) { lcm = b ; break ; } } printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }