int gcd ( int a , int b ) ; int lcm ( int x , int y ) ; int main ( ) { int tc , a , b , gc , lc ; scanf ( "%d" , & tc ) ; while ( tc != 0 ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { gc = gcd ( a , b ) ; lc = lcm ( a , b ) ; } else { gc = gcd ( b , a ) ; lc = lcm ( b , a ) ; } printf ( "%d %d\n" , gc , lc ) ; tc -- ; } return 0 ; } gcd ( int a , int b ) { int c ; while ( a != 0 ) { c = a ; a = b % a ; b = c ; } return b ; } int lcm ( int x , int y ) { int max ; max = ( x > y ) ? x : y ; while ( 1 ) { if ( max % x == 0 && max % y == 0 ) { return max ; } ++ max ; } }