int find_gcd ( int a , int b ) { int max , min , temp ; max = a > b ? a : b ; min = a < b ? a : b ; while ( max % min ) { temp = min ; min = max % min ; max = temp ; } return min ; } int main ( ) { int t , n , a , b , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; gcd = find_gcd ( a , b ) ; lcm = a * b / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } }