int GCD ( int q , int w ) { int i , c = 1 , s ; if ( q > w ) s = w ; else s = q ; for ( i = 1 ; i <= s ; i ++ ) { if ( ( q % i == 0 ) && ( w % i == 0 ) ) { if ( i == s ) { c = s ; break ; } else { c = i ; continue ; } } } return c ; } void main ( ) { int i , j , g , gcd , lcm , x , y ; short n ; scanf ( "%hi" , & n ) ; int a [ n ] [ 2 ] ; for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j < 2 ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; for ( i = 0 ; i < n ; i ++ ) { lcm = 1 ; x = a [ i ] [ 0 ] ; y = a [ i ] [ 1 ] ; g = GCD ( x , y ) ; gcd = g ; while ( gcd > 1 ) { x = x / gcd ; y = y / gcd ; lcm = lcm * gcd ; gcd = GCD ( x , y ) ; } if ( g == 1 ) lcm = x * y ; else lcm = lcm * x * y ; printf ( "%d %d\n" , g , lcm ) ; } }