int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( ) { int t ; int a , b , c , d ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; c = gcd ( a , b ) ; d = lcm ( a , b ) ; printf ( "%d\t%d\n" , c , d ) ; } return 0 ; } int gcd ( int x , int y ) { if ( x % y == 0 ) return y ; else return gcd ( y , x % y ) ; } int lcm ( int x , int y ) { int a = x ; int b = y ; while ( x != y ) { if ( x > y ) y = b + y ; else x = a + x ; } return x ; }