int main ( ) { int a , b , i , d = 0 , n , m , k = 0 , j ; scanf ( "%d" , & j ) ; while ( j > 0 ) { printf ( "\n" ) ; scanf ( "%d%d" , & a , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) d = i ; } n = a / d ; m = b / d ; k = d * n * m ; printf ( "%d  %d" , d , k ) ; j -- ; } return 0 ; }