int gcd ( int x , int y ) { int p , m = 1 ; if ( x < y ) { x = x + y ; y = x - y ; x = x - y ; } while ( m != 0 ) { m = x % y ; if ( m == 0 ) return y ; else x = y ; y = m ; } } int main ( ) { int T , i ; scanf ( "%d" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { int lcm , x , y , m ; scanf ( "%d%d" , & x , & y ) ; m = gcd ( x , y ) ; lcm = x * y / m ; printf ( "the gcd and lcm of %d and %d is %d %d\n" , x , y , m , lcm ) ; } return 0 ; }