int main ( ) { int t , n , i , j , a [ 50000 ] , cost , k , l ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { k = 0 , cost = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; for ( l = k + 1 ; l < n ; l ++ ) { if ( a [ k ] > a [ l ] ) { cost = cost + a [ l ] ; k ++ ; } else { cost = cost + a [ k ] ; } } printf ( "%d\n" , cost ) ; } return 0 ; }