int main ( ) { long long int i , t , n , a , b , j , c , l ; scanf ( "%lld" , & t ) ; for ( i = t ; i > 0 ; i -- ) { scanf ( "%lld %lld" , & a , & b ) ; if ( a >= b ) { for ( j = b ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { c = j ; break ; } } } else { for ( j = a ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { c = j ; break ; } } } printf ( "%lld %lld \n" , c , a * b / c ) ; } }