int main ( ) { int t , flag ; long int n , num , min , i ; long long int r ; scanf ( "%d" , & t ) ; while ( t -- ) { flag = 0 ; scanf ( "%ld" , & n ) ; if ( n < 2 ) continue ; scanf ( "%ld" , & min ) ; for ( i = 1 ; i < n ; i ++ ) { scanf ( "%ld" , & num ) ; if ( min > num ) min = num ; else if ( min == num ) { flag = 1 ; break ; } } if ( flag ) continue ; r = min * ( n - 1 ) ; printf ( "\t%lld\n" , r ) ; } return 0 ; }