int gcd ( ) ; int main ( ) { int t , a , b , g , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a > b ) return gcd ( ( a - b ) , b ) ; else if ( b > a ) return gcd ( a , ( b - a ) ) ; }