int GCD ( int a , int b ) { int c ; while ( b ) { c = b ; b = a % b ; a = c ; } return a ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int a , b , gcd , lcm ; scanf ( "%lld %lld" , & a , & b ) ; gcd = GCD ( a , b ) ; lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }