int main ( ) { int t , n , arr [ 50000 ] , i , min ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; min = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) if ( arr [ i ] < min ) min = arr [ i ] ; printf ( "%d\n" , min * ( n - 1 ) ) ; } return 0 ; }