int main ( void ) { int t , * p , n , i , j ; scanf ( "%d" , & t ) ; while ( t -- ) { int min = 0 ; scanf ( "%d" , & n ) ; p = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & p [ i ] ) ; } min = * p ; for ( j = 0 ; j < n ; j ++ ) { if ( p [ j ] < p [ j + 1 ] ) min = p [ j ] ; } printf ( "%d \n" , min * ( n - 1 ) ) ; free ( p ) ; } return 0 ; }