int main ( ) { int k , l , r ; scanf ( "%d" , & l ) ; int total [ l ] ; for ( r = 0 ; r < l ; r ++ ) { int m = 0 , j , i , sum = 0 , sum1 = 0 ; scanf ( "%d" , & k ) ; int a [ k ] [ k ] ; for ( i = 0 ; i < k ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } total [ r ] = a [ 0 ] [ 0 ] ; for ( i = 1 ; i < k ; i ++ ) { sum = total [ r ] + a [ i ] [ m ] ; sum1 = total [ r ] + a [ i ] [ m + 1 ] ; if ( sum > sum1 ) total [ r ] = total [ r ] + a [ i ] [ m ] ; else { total [ r ] = total [ r ] + a [ i ] [ m + 1 ] ; m = m + 1 ; } } } for ( r = 0 ; r < l ; r ++ ) { printf ( "%d\n" , total [ r ] ) ; } return 0 ; }