int main ( ) { int nTestCases ; long long nArrIntegers [ 50000 ] , nIntegerCount , nMinCost ; long long i , nSmallestInt = 1000000 ; scanf ( "%d" , & nTestCases ) ; while ( nTestCases != 0 ) { scanf ( "%lld" , & nIntegerCount ) ; PRINTD ( "%lld Integers\n" , nIntegerCount ) ; for ( i = 0 ; i <= nIntegerCount - 1 ; i ++ ) { scanf ( "%lld" , & nArrIntegers [ i ] ) ; PRINTD ( "%d is current Integer\n" , nArrIntegers [ i ] ) ; if ( nArrIntegers [ i ] < nSmallestInt ) { nSmallestInt = nArrIntegers [ i ] ; } } nMinCost = nSmallestInt * ( nIntegerCount - 1 ) ; printf ( "%lld\n" , nMinCost ) ; nTestCases -- ; } return 0 ; }