long int gcd ( long int , long int ) ; int main ( ) { long int x , y , g , lcm , t , i ; scanf ( "%ld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & x , & y ) ; g = gcd ( x , y ) ; lcm = ( x * y ) / g ; printf ( "%ld %ld\n" , g , lcm ) ; } return 0 ; } long int gcd ( long int a , long int b ) { if ( a > b ) { if ( a % b == 0 ) return b ; else return gcd ( b , a % b ) ; } else if ( b % a == 0 ) return a ; else return gcd ( a , b % a ) ; }