int main ( ) { int t , b , a , i , s , g = 1 , l = 1 , j ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a < b ) s = a ; else s = b ; for ( j = 1 ; j <= s ; j ++ ) { if ( a % j == 0 && b % j == 0 ) g = j ; } for ( j = s ; j <= ( a * b ) ; j = j + s ) { if ( j % a == 0 && j % b == 0 ) { l = j ; break ; } } printf ( "%d %d\n" , g , l ) ; } return 0 ; }