int main ( ) { int T , A , B , i , j , LCM , GCD , s ; scanf ( "%d" , & T ) ; while ( T > 0 ) { s = 1 ; scanf ( "%d%d" , & A , & B ) ; for ( i = A * B ; i > 2 ; i -- ) if ( i % A == 0 && i % B == 0 ) LCM = i ; if ( A % B != 0 && B % A != 0 ) GCD = 1 ; else { for ( i = 1 ; i < 1000 ; i ++ ) if ( A % i == 0 && B % i == 0 ) GCD = i ; } printf ( "%d %d\n" , GCD , LCM ) ; T -- ; } return 0 ; }