int gcd ( int a , int b ) ; int main ( ) { int t , i , a , b , c ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; c = a * b ; printf ( "%d " , gcd ( a , b ) ) ; printf ( "%d\n" , c / gcd ( a , b ) ) ; } } int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; }