void main ( ) { int i , n , j , sum = 0 , t ; int * ptr ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & n ) ; ptr = malloc ( n * sizeof ( n ) ) ; for ( j = 1 ; j <= n ; j ++ ) { scanf ( "%d" , ptr + j ) ; } j = 1 ; while ( j != ( n ) ) { if ( ptr + j > ptr + j + 1 ) { * ( ptr + j ) = * ( ptr + j ) ; sum = sum + ( * ( ptr + j + 1 ) ) ; } else { * ( ptr + j + 1 ) = * ( ptr + j ) ; sum = sum + ( * ( ptr + j + 1 ) ) ; } j ++ ; } printf ( "%d\n" , sum ) ; sum = 0 ; } return 0 ; }