int main ( ) { int t , t0 ; scanf ( "%d" , & t ) ; for ( t0 = 0 ; t0 < t ; t0 ++ ) { int n , m , i , j , k = 0 ; scanf ( "%d" , & n ) ; m = ( n * ( n + 1 ) ) / 2 ; int a [ m ] ; for ( i = 0 ; i < m ; i ++ ) { scanf ( "%d" , a + i ) ; } j = n ; for ( i = m - 1 ; i > 1 ; i -- ) { k ++ ; if ( a [ i ] > a [ i - 1 ] ) { a [ i - j ] += a [ i ] ; } else a [ i - j ] += a [ i - 1 ] ; if ( k == j - 1 ) { k = 0 , j -- , i -- ; } } printf ( "%d\n" , a [ 0 ] ) ; } return 0 ; }