main ( ) { int t = 0 ; int i = 0 , j = 0 ; int a = 0 , b = 0 ; int gcd = 0 , lcm = 0 ; int lcmm = 0 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; for ( j = 1 ; j <= a ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; } } lcmm = a * b ; for ( j = b ; j <= lcmm ; j ++ ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; } } printf ( "%d %d" , gcd , lcm ) ; } }