int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int i , a , prev2 = 0 , prev = 0 , n , count = 0 ; scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a ) ; if ( i == n - 1 && a > prev && n != 1 ) { count ++ ; break ; } if ( a > prev ) { prev2 = prev ; prev = a ; continue ; } if ( prev2 < prev ) count ++ ; prev2 = prev ; prev = a ; } count += n ; printf ( "%lld\n" , count ) ; } return 0 ; }