void out ( long int a , long int b , long int x ) { long int i ; long long int y , g = 1 ; for ( i = 1 ; i <= x ; i ++ ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) g = i ; } printf ( "%lld " , g ) ; y = ( a * b ) / g ; printf ( "%lld\n" , y ) ; } int main ( ) { long int n , x ; scanf ( "%ld " , & n ) ; long int i , a , b ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a >= b ) x = b ; else x = a ; out ( a , b , x ) ; } return 0 ; }