int main ( ) { int t , n , count ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { count = 0 ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( int j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & arr [ j ] ) ; } while ( n != 1 ) { int x = 0 ; if ( arr [ 0 ] > arr [ 1 ] ) { count += arr [ 1 ] ; while ( x < n - 1 ) { arr [ x ] = arr [ x + 1 ] ; x ++ ; } n -- ; } else { count += arr [ 0 ] ; x = 1 ; while ( x < n - 1 ) { arr [ x ] = arr [ x + 1 ] ; x ++ ; } n -- ; } } printf ( "%d\n" , count ) ; } return 0 ; }