main ( ) { int test , n , i , sum ; scanf ( "%d" , & test ) ; while ( test -- ) { scanf ( "%d" , & n ) ; int a [ n ] ; int ans [ n ] ; sum = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } ans [ 0 ] = 1 ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i - 1 ] <= a [ i ] ) ans [ i ] = ans [ i - 1 ] + 1 ; else ans [ i ] = 1 ; sum += ans [ i ] ; } printf ( "%d\n" , sum + 1 ) ; } }