int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , j , a [ 101 ] [ 101 ] = { 0 } , count = 0 , k = 0 ; scanf ( "%d" , & n ) ; int row = 0 , coloumn = 0 , max = 0 ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; if ( max < a [ i ] [ j ] ) { max = a [ i ] [ j ] ; row = i ; coloumn = j ; k = j ; } } } for ( i = row ; i >= 0 ; i -- ) { if ( coloumn == 0 || a [ i ] [ coloumn ] >= a [ i ] [ coloumn - 1 ] ) count = count + a [ i ] [ coloumn ] ; else { count = count + a [ i ] [ coloumn - 1 ] ; coloumn = coloumn - 1 ; } } for ( i = row + 1 ; i < n ; i ++ ) { if ( k == n - 1 || a [ i ] [ k ] >= a [ i ] [ k + 1 ] ) { count = count + a [ i ] [ k ] ; } else { count = count + a [ i ] [ k + 1 ] ; k = k + 1 ; } } printf ( "%d\n" , count ) ; } return 0 ; }