int main ( ) { int t , i , a , b , n1 , n2 , g , l ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & n1 , & n2 ) ; if ( n1 >= n2 ) { a = n1 ; b = n2 ; } else { a = n2 ; b = n1 ; } int j = b ; for ( j = b ; j > 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { g = j ; j = 0 ; } } for ( j = a ; j <= a * b ; j ++ ) { if ( j % a == 0 && ( j % b ) == 0 ) { l = j ; j = a * b + 1 ; } } printf ( "%d %d\n" , g , l ) ; } return 0 ; }