int main ( ) { int t , n , i , j , k , c , s ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = n - 1 ; j >= 0 ; j -- ) { scanf ( "%d" , & a [ j ] ) ; } for ( k = n - 1 ; k >= 0 ; k -- ) { if ( a [ k - 1 ] > a [ k ] ) { c = a [ k ] ; a [ k ] = a [ k - 1 ] ; a [ k - 1 ] = c ; } } s = a [ 0 ] * ( n - 1 ) ; printf ( "%d\n" , s ) ; } return 0 ; }