long long int gcd ( int a , int b ) { if ( b == 0 || a == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } main ( ) { int t , a , b ; long long int gcd1 , lcm1 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; gcd1 = gcd ( a , b ) ; lcm1 = lcm ( a , b ) ; printf ( "%lld" , gcd1 ) ; printf ( " " ) ; printf ( "%lld\n" , lcm1 ) ; } }