int main ( ) { int t , i , j , n , next , prev ; long long int ans ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int A [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & A [ j ] ) ; prev = 0 ; next = 1 , ans = 0 ; while ( next < n ) { if ( A [ prev ] > A [ next ] ) { ans += A [ next ] ; prev = next ; next ++ ; } else if ( A [ prev ] < A [ next ] ) { ans += A [ prev ] ; next ++ ; } } printf ( "%lld\n" , ans ) ; } return 0 ; }