int main ( ) { int n , i , t , j , k , sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%d" , & t ) ; int x [ t ] [ t ] ; for ( j = 1 ; j <= t ; j ++ ) { for ( k = 1 ; k <= j ; k ++ ) { scanf ( "%d" , & x [ j ] [ k ] ) ; if ( j > 1 && k >= 2 ) { if ( sum <= ( x [ j - 1 ] [ k - 1 ] + x [ j ] [ k - 1 ] + x [ j ] [ k ] ) ) sum = x [ j - 1 ] [ k - 1 ] + x [ j ] [ k - 1 ] + x [ j ] [ k ] ; } } } printf ( "%d \n" , sum ) ; } return 0 ; }