int main ( ) { int T ; scanf ( "%d" , & T ) ; for ( int i = 0 ; i < T ; ++ i ) { long int A , B , hcf = 1 ; scanf ( "%ld %ld" , & A , & B ) ; long int smaller = A < B ? A : B ; long int tempA = A , tempB = B ; for ( long int j = 2 ; j <= smaller ; ++ j ) { while ( A % j == 0 && B % j == 0 ) { hcf *= j ; A /= j ; B /= j ; } } long long int lcm = A * B / hcf ; printf ( "%ld %lld \n" , hcf , lcm ) ; } return 0 ; }