int main ( ) { int t ; int a , b , UCLN , BCNN , chay ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; chay = 1 ; while ( chay <= a && chay <= b ) { if ( ( a % chay == 0 ) && ( b % chay == 0 ) ) { UCLN = chay ; } chay ++ ; } chay = a ; while ( chay ++ ) { if ( ( chay % a == 0 ) && ( chay % b == 0 ) ) { BCNN = chay ; break ; } } printf ( "%d %d\n" , UCLN , BCNN ) ; } return 0 ; }