int main ( ) { int t , n , a , i = 0 , c = 0 , p , b , l ; scanf ( "%d" , & t ) ; for ( b = 1 ; b <= t ; b ++ ) { scanf ( "%d" , & n ) ; int z [ n ] , s [ n ] ; i = 0 ; c = 0 ; for ( a = 0 ; a < n ; a ++ ) { scanf ( "%d" , & z [ a ] ) ; s [ a ] = z [ a ] ; } p = 0 ; while ( n - 1 > i ) { if ( s [ p ] > s [ p + 1 ] ) { c = s [ p + 1 ] + c ; i = i + 1 ; a = s [ p + i + 1 ] ; s [ p ] = a ; } else { c = s [ p ] + c ; i = i + 1 ; l = s [ p + i + 1 ] ; s [ p + 1 ] = l ; } } printf ( "%d" , c ) ; } return 0 ; }