int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , i , l = 1 , g = 0 ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { for ( i = 2 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } printf ( "%d\n" , g ) ; } else { for ( i = 2 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } printf ( "%d\n" , g ) ; } if ( a > b ) { for ( i = 2 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { l = l * i ; a = a / i ; b = b / i ; } } l = l * a * b ; printf ( "%d\n" , l ) ; } else { for ( i = 2 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { l = l * i ; a = a / i ; b = b / i ; } } l = l * a * b ; printf ( "%d\n" , l ) ; } } return 0 ; }