int GCD ( int a , int b ) ; int main ( ) { int n , i , j , k ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%d %d" , & j , & k ) ; printf ( "%d %d\n" , GCD ( j , k ) , j * k / GCD ( j , k ) ) ; } return 0 ; } int GCD ( int a , int b ) { int e ; int f ; int gcd ; int g ; if ( a > b ) { e = a ; f = b ; } else if ( a < b ) { e = b ; f = a ; } else { gcd = a ; } if ( e % f == 0 ) { gcd = f ; } while ( e % f != 0 ) { g = e % f ; e = f ; f = g ; if ( e % f == 0 ) { gcd = f ; } } return gcd ; }