int main ( ) { int t , n , i , min ; int * p , * ptr ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; p = ( int * ) malloc ( n * sizeof ( int ) ) ; ptr = p ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%d" , p ) ; p ++ ; } min = * ptr ; ptr ++ ; for ( i = 1 ; i < n - 1 ; i ++ ) { if ( * ptr < min ) min = * ptr ; } min = min * ( n - 1 ) ; printf ( "%d\n" , min ) ; free ( p ) ; } return 0 ; }