void gcd ( int , int ) ; void lcm ( int , int ) ; int main ( ) { int t , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; gcd ( a , b ) ; lcm ( a , b ) ; } return 0 ; } void lcm ( int x , int y ) { int a , b ; a = x ; b = y ; while ( x != y ) { if ( x > y ) y = b + y ; else x = a + x ; } printf ( "%d\n" , x ) ; } void gcd ( int x , int y ) { while ( x != y ) { if ( x > y ) x = x - y ; else y = y - x ; } printf ( "%d\t" , x ) ; }