int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a ; scanf ( "%ld" , & a ) ; long int b [ 50001 ] ; long int i ; int l , k ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%ld" , & b [ i ] ) ; } if ( b [ 0 ] > b [ 1 ] ) { k = b [ 1 ] ; } else { k = b [ 0 ] ; } for ( i = 1 ; i < a ; i ++ ) { if ( b [ i ] < k ) { k = b [ i ] ; } } l = ( a - 1 ) * k ; printf ( "%d\n" , l ) ; } return 0 ; }