int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int tc = 0 ; tc < t ; tc ++ ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( int i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; int c = 0 ; int j ; for ( int i = 0 ; i < n ; i ++ ) { j = i + 1 ; if ( j < n && a [ i ] <= a [ j ] ) { c ++ ; while ( a [ j ] <= a [ j + 1 ] && j + 1 < n ) { c ++ ; j ++ ; } } } long long int res = c + n ; printf ( "%lld\n" , res ) ; } return 0 ; }